package Lecture2;

public class Loops_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// for loop
		for (int i = 0; i < 5; i++) {
			System.out.println(i);
		}

		// while loop
		int i = 0;
		while (i < 5) {
			System.out.println(i);
			i++;
		}
	}

}
