package Lecture2;

public class Variables {
	static int a = 12;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.println(a);

		
		// for loop
		for (int i = 0; i < 5; i++) {
			System.out.println(i);
		}

		// while loop
		int i = 0;
		while (i < 5) {
			System.out.println(i);
			i++;
			
		}
	}

}
